import React from 'react'
import { connect } from 'react-redux'
import { selectSong } from "../actions";

const SongDetail = (props) => {
    return (
        <div>
            {props.song.title}
            {props.song.duration}
        </div>
    )
}

const mapStateToProps = state => {
    return {song: state.selectedSong}
}

export default connect(
    mapStateToProps
)(SongDetail)
