import React from 'react'
import wikipedia from "../api/wikipedia";

class Search extends React.Component {
    state={term: '', results: []}

    onSearchSubmit = async (event) => {
        event.preventDefault()
        console.log("Search term is " + this.state.term)
        const resp = await wikipedia.get("", {
            params: {srsearch: this.state.term}
        })
        this.setState({results: resp.data.query.search})
        console.log(resp.data.query.search)
    }

    render() {
        /*
        const descriptions = this.results.map((desc) => {
            return (
                <React.Fragment key={desc.title}>
                    <h4>{desc.title}</h4>
                    <p>{desc.snippet}</p>
                </React.Fragment>
            )
        })
        <div className={"ui styled accordion"}>{descriptions}</div>
        */
        return (
            <div className={"search-bar ui sergment"}>
                <form className={"ui form"} onSubmit={this.onSearchSubmit}>
                    <div className={"field"}>
                        <label> Search Wikipedia </label>
                        <input
                            type={"text"}
                            value={this.state.term}
                            onChange={(e) => this.setState({term: e.target.value})}
                        />
                    </div>
                </form>
            </div>
        )
    }
}

export default Search
