import React, {useEffect, useState} from 'react'
import wikipedia from "../api/wikipedia";

const Search = () => {
    const [term, setTerm] = useState('')
    const [results, setResults] = useState([])

    useEffect( () => {
        const search = async () => {
            const {data}  = await wikipedia.get("", {
                params: {srsearch: term}
            })
        }
        search()
        console.log(data.query.search)
    }, [term])


    const descriptions = this.state.results.map((desc) => {
        return (
            <React.Fragment key={desc.title}>
                <h4>{desc.title}</h4>
                <p>{desc.snippet}</p>
            </React.Fragment>
        )
    })

    return (
        <div className={"search-bar ui sergment"}>
            <form className={"ui form"} onSubmit={useEffect}>
                <div className={"field"}>
                    <label> Search Wikipedia </label>
                    <input
                        type={"text"}
                        value={term}
                        onChange={(e) => setTerm(e.target.value)}
                    />
                </div>
            </form>
            <div className={"ui styled accordion"}>{descriptions}</div>
        </div>
    )
}

export default Search
