import React, {useEffect, useState} from 'react'
import wikipedia from "../api/wikipedia";

const Search = () => {
    const [term, setTerm] = useState('')
    const [results, setResults] = useEffect()

    const onSearchSubmit = async (event) => {
        event.preventDefault()
        console.log("Search term is " + term)
        const resp = await wikipedia.get("", {
            params: {srsearch: term}
        })
        setResults(resp.data.query.search)
        console.log(resp.data.query.search)
    }



    const descriptions = this.state.results.map((desc) => {
        return (
            <React.Fragment key={desc.title}>
                <h4>{desc.title}</h4>
                <p>{desc.snippet}</p>
            </React.Fragment>
        )
    })

    return (
        <div className={"search-bar ui sergment"}>
            <form className={"ui form"} onSubmit={this.onSearchSubmit}>
                <div className={"field"}>
                    <label> Search Wikipedia </label>
                    <input
                        type={"text"}
                        value={term}
                        onChange={(e) => setTerm(e.target.value)}
                    />
                </div>
            </form>
            <div className={"ui styled accordion"}>{descriptions}</div>
        </div>
    )
}

export default Search
