import React from 'react'
import wikipedia from "../api/wikipedia";

class Search extends React.Component {
    state={term: '', results: []}

    onSearchSubmit = async (term) => {
        const resp = await wikipedia.get("", {
            params: {srsearch: term}
        })
        this.setState({results: resp.query.search})
    }

    render() {

        return (
            <div className={"search-bar ui sergment"}>
                <form className={"ui form"} onSubmit={this.onSearchSubmit}>
                    <div className={"field"}>
                        <label> Search Wikipedia </label>
                        <input
                            type={"text"}
                            value={this.state.term}
                            onChange={(e) => this.setState({term: e.target.value})}
                        />
                    </div>
                </form>
                <div className={"ui styled accordion"}>{descriptions}</div>
            </div>
        )
    }
}

export default Search
