import React, {useState} from 'react'


const Dropdown = ({options, selected, onSelectedChanged}) => {
    const cards = options.map(option => {
        return (
            <div
                className={"item"}
                key={option.value}
                onClick={onSelectedChanged}
            >
                {option.label}
            </div>
        )
    })
    return (
        <div className={"ui form"}>
            <div className={"field"}>

            </div>

        </div>
    )
}

export default Dropdown
