import React from 'react';
import SearchBar from "./SearchBar";
import VideoDetail from "./VideoDetail";
import VideoList from "./VideoList";
import youtube from "../apis/youtube";

class App extends React.Component {
    state={videos: [], selectedVideo: null}

    componentDidMount() {
        this.onSearchSubmit("the office")
    }

    onSearchSubmit = async (term) => {
        const youtube_resp = await youtube.get("/search", {
            params: {q: term}
        })
        console.log(youtube_resp)
        this.setState({
            videos: youtube_resp.data.items,
            selectedVideo: youtube_resp.data.items[0]
        })
    }
    onVideoClick = (video) => {
        this.setState({selectedVideo: video})
    }
    render() {
        return (
            <div className={"ui container"}>
                <SearchBar onSubmit={this.onSearchSubmit}/>
                <div className={"ui grid"}>
                    <div className={"ui row"}>
                        <div className={"eleven wide column"}>
                            <VideoDetail video={this.state.selectedVideo}/>
                        </div>
                        <div className={"five wide column"}>
                            <VideoList videos={this.state.videos} selectedVideo={this.onVideoClick}/>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default App
