import React, {useEffect, useState} from 'react';
import SearchBar from "./SearchBar";
import VideoDetail from "./VideoDetail";
import VideoList from "./VideoList";
import youtube from "../apis/youtube";


const App = () => {
    const [videos, setVideos] = useState([])
    const [selectedVideo, setSelectedVideo] = useState(null)

    const onSearchSubmit = async (term) => {
        const youtube_resp = await youtube.get("/search", {
            params: {q: term}
        })
        setVideos(youtube_resp.data.items)
        setSelectedVideo(youtube_resp.data.items[0])
    }

    useEffect(() => {
        onSearchSubmit("the office")
    }, [])

    return (
        <div className={"ui container"}>
            <SearchBar onSubmit={onSearchSubmit}/>
            <div className={"ui grid"}>
                <div className={"ui row"}>
                    <div className={"eleven wide column"}>
                        <VideoDetail video={selectedVideo}/>
                    </div>
                    <div className={"five wide column"}>
                        <VideoList videos={videos} selectedVideo={(video) => setSelectedVideo(video)}/>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default App
