import React from 'react';
import SearchBar from "./SearchBar";
import VideoDetail from "./VideoDetail";
import VideoList from "./VideoList";
import unsplash from "../apis/unsplash";
import youtube from "../apis/youtube";

class App extends React.Component {
    state={term: "", images: [], videos: []}
    onSearchSubmit = async (term) => {
        const youtube_resp = await youtube.get("/search", {
            params: {q: term}
        })
        console.log(youtube_resp)
        this.setState({videos: youtube_resp.data.items})
        //const resp = await unsplash.get("/search/photos", {
        //    params: {query: term}
        //})
        //this.setState({term: term, images: resp.data.results})
    }
    render() {
        return (
            <div className={"ui container"}>
                <SearchBar onSubmit={this.onSearchSubmit}/>
                <div className={"ui video container"}>
                    <VideoDetail term={this.state.term} images={this.state.images}/>
                    <VideoList videos={this.state.videos}/>
                </div>
            </div>
        )
    }
}

export default App
